//       onChange={props.onImage}
//       imageExtension={['.jpg','.png','jpeg']}
//       maxFileSize={5242880}></ImageUploder>
//   </form>
// }

const App = () => {

  const [progress,setProgress] = useState ('getUplaod')
  const [url, setImageURL] = useState (undefined)
  const [errorMessage, setErrorMessage]= useState('')
  const onUrlChange = e => {
    setImageURL(e.target.value);
  };
  const onImage=async(failedImages, successImages) => {
    if (!url)
    {
      console.log('missing Url')
      setErrorMessage('missing a url to upload')
      setProgress('uploadError');
      return
    }
    setProgress('uploading')

      try {
        console.log('successImages',successImages)
      }

      catch (error){
        console.log('error in upload',error);
        setErrorMessage(error.message);
        setProgress('uploadError')

      }
  }
  const content = () =>
  {
    switch(progress){
      case 'getUplaod':
        return <UploadComponent onUrlChange={onUrlChange} onImage={onImage} url={url}/>
      case 'uploading':
        return <h2>Uploading...</h2>
      case 'uploaded':
        return <img scr={url} alt='uploaded' />
      case 'uploadError':
        return (
          <>
          <div>Error Message={errorMessage}</div>
          <div>Please upload an image</div>
          </>
        )
    }
  }

  return (
    <div className="App">
      <h1>Image Upload </h1>
      {content()}
    </div>
  )
}

export default App
