import logo from './logo.svg';
import './App.css';
import { useState } from 'react';
const App = () => {

  const [progress,setProgress] = useState ('getUplaod')
  const [url, setImageURL] = useState [undefined]
  const [errorMessage, srtErrorMessage]= useState('')
  const content = () =>
  {
    switch(progress){
      case 'getUpload':
        return <div>Please upload an image</div>
      case 'uploading':
        return <h2>Uploading...</h2>
      case 'uploaded':
        return <img scr={url} alt='uploaded' />
      case 'uploadError':
        return (
          <>
          <div>Error Message={errorMessage}</div>
          <div>Please upload an image</div>
          </>
        )
    }
  }

  return (
    <div className="App">
      <h1>Image Upload </h1>
      {content()}
    </div>
  )
}

export default App
